mapper.update:
update member set
member_pw = #{member_pw}
where member_no = ${member_no}

UPDATE MEMBER
SET STORED_CRED = #{stored_cred}
WHERE MEMBER_APPROVE='Y' AND MEMBER_EMAIL = #{member_email}

UPDATE MEMBER
SET STORED_CRED = null
WHERE MEMBER_EMAIL = #{member_email}

mapper.select:
SELECT STORED_CRED
FROM MEMBER
WHERE MEMBER_EMAIL = #{member_email}
