mapper.select:
SELECT DECODE(COUNT(1), 0, 'N', 'Y') AS EXISTS_YN
	FROM MEMBER
	WHERE MEMBER_EMAIL = #{member_email}

SELECT DECODE(COUNT(1), 0, 'N', 'Y') AS EXISTS_YN
	FROM MEMBER
	WHERE MEMBER_ID = #{member_id}

SELECT MEMBER_ID, MEMBER_NAME, MEMBER_EMAIL
FROM MEMBER
WHERE MEMBER_ID =#{member_id}
AND PASSWORD =#{member_pw}
